mapper.sql:
where 1=1

and gameid=#{gameid}


and channel=#{channelid}


and areaid=#{areaid}


and statdate>=#{startdate}


and statdate>=#{startdate} and statdate <=#{enddate}


and statdate<=#{enddate}

where 1=1

and a.gameid=#{gameid}


and a.channel=#{channelid}


and a.areaid=#{areaid}


and a.statdate>=#{startdate}


and a.statdate>=#{startdate} and a.statdate <=#{enddate}


and a.statdate<=#{enddate}

mapper.select:
SELECT a.statdate, SUBSTR( date_format(a.logdate, '%Y%m%d%H'), 9, 10 ) date, count(DISTINCT a.uid, a.actorid) persons, round(count(DISTINCT a.uid, a.actorid) / b.count1*100,2) personsaf, sum(a.money) money, round(sum(a.money) / c.count2*100,2) moneyaf FROM logplat_actor_recharge a, ( select sum(c.count1) count1 from ( SELECT count(DISTINCT uid, actorid) count1 FROM logplat_actor_recharge
group by SUBSTR( date_format(logdate, '%Y%m%d%H'), 9, 10 ) )c
) b, ( SELECT sum(money) count2 FROM logplat_actor_recharge
) c
GROUP BY SUBSTR( date_format(logdate, '%Y%m%d%H'), 9, 10 )
